/// <reference types="node" />
import type { JsonValue } from "type-fest";
export interface DownloadFileOptions {
    url: string;
    gzip?: boolean;
    timeout?: number;
}
export interface DownloadFileTicket<T> {
    url: string;
    promise: Promise<T>;
    cancel(): void;
}
export declare function downloadFile({ url, timeout, gzip }: DownloadFileOptions): DownloadFileTicket<Buffer>;
export declare function downloadJson(args: DownloadFileOptions): DownloadFileTicket<JsonValue>;
